import React from 'react';

import './ModalPopUp.css';

const ModalPopUp =(props)=>{
    const showHideModal = props.show?"modal display-block":"modal display-none";
    const formElementsArray = [];
        for (let key in props.formEleArr) {
            formElementsArray.push({
                id: key,
                config: this.state.orderForm[key]
            });
        }
        let form = (
            <form onSubmit={this.orderHandler}>
                {formElementsArray.map(formElement => (
                    <Input 
                        key={formElement.id}
                        elementType={formElement.config.elementType}
                        elementConfig={formElement.config.elementConfig}
                        value={formElement.config.value}
                        changed={(event) => this.inputChangedHandler(event, formElement.id)} />
                ))}
                <Button btnType="Success">ORDER</Button>
            </form>
        );
   return(
       <div className={showHideModal}>
           <div className="modal-main form-horizontal">
                <button onClick={props.handleclose}>close</button>
                <div className="row">
                        <h3>Add New User</h3>
                </div>
                <div className="row form-group">
                    <div className="col-md-6 col-md-offset-4">
                        <div className="col-md-2"><label>Fullname:</label></div>
                        <div className="col-md-10 "><input type="text" className="form-control"></input></div>
                    </div>    
                </div>
                <div className="row form-group">
                    <div className="col-md-6 col-md-offset-4">
                        <div className="col-md-2"><label>DOB:</label></div>
                        <div className="col-md-10"><input type="text" className="form-control"></input></div>
                    </div>    
                </div>
                <div className="row form-group">
                    <div className="col-md-6 col-md-offset-4">
                        <div className="col-md-3"><label>Languages:</label></div>
                        <div className="col-md-9"><input type="text" className="form-control"></input></div>
                    </div>    
                </div>
                <div className="row form-group">
                    <div className="col-md-6 col-md-offset-4">
                        <div className="col-md-2"><label>Gender:</label></div>
                        <div className="col-md-10"><input type="text" className="form-control"></input></div>
                    </div>    
                </div>
                <div className="row form-group">
                    <div className="col-md-6 col-md-offset-4">
                        <div className="col-md-2"><label>About:</label></div>
                        <div className="col-md-10"><input type="text" className="form-control"></input></div>
                    </div>    
                </div>
                <button className="button button-info">Save New User</button>
           </div>
       </div>
   )
}

export default ModalPopUp;
