import React, {useState, useEffect, useRef} from 'react';

import Card from '../UI/Card';
import './Search.css';

const Search = React.memo(props => {
 const { onLoadIngredients } = props;
 const [enteredFilter, setEnteredFilter] = useState('');
 const inputRef = useRef();
 useEffect(()=>{
   setTimeout(()=>{
        if(enteredFilter === inputRef.current.value){
          const query = enteredFilter.length ===0?"":`?orderBy="title"&equalTo="${enteredFilter}"`;
          fetch("https://react-hooks-c4ac3.firebaseio.com/ingredients.json"+query).then(response =>response.json()).then(responseData =>{
            const temporaryDataHolder = [];
            for(const key in responseData){
              temporaryDataHolder.push({
                id: key,
                title: responseData[key].title,
                amount: responseData[key].amount
              });
            }
            onLoadIngredients(temporaryDataHolder);
          })
        }
   },500)
 },[enteredFilter, onLoadIngredients]);
  return (
    <section className="search">
      <Card>
        <div className="search-input">
          <label>Filter by Title</label>
          <input type="text" ref={inputRef} onChange={event => setEnteredFilter(event.target.value)}/>
        </div>
      </Card>
    </section>
  );
});

export default Search;
