import React, {useState, useEffect, useCallback} from 'react';

import IngredientForm from './IngredientForm';
import Search from './Search';
import IngredientsList from './IngredientList';

const Ingredients= () => {
  const [ userIngredients, setUserIngredients] = useState([]);

  useEffect(()=>{
      fetch("https://react-hooks-c4ac3.firebaseio.com/ingredients.json").then(response =>response.json()).then(responseData =>{
        const temporaryDataHolder = [];
        for(const key in responseData){
          temporaryDataHolder.push({
            id: key,
            title: responseData[key].title,
            amount: responseData[key].amount
          });
        }
        setUserIngredients(temporaryDataHolder);
      })
  },[]);

  const ingredientSubmitHandler = ( ingredient ) =>{
    fetch("https://react-hooks-c4ac3.firebaseio.com/ingredients.json",{
      method:'POST',
      body: JSON.stringify(ingredient),
      headres:{'Content-Type': 'application/json'}
    }).then(response =>{
      return response.json();
      console.log(response.json());
    }).then(responseData =>{
      setUserIngredients( prevIngredients => [...prevIngredients, {id:responseData.name, ...ingredient}])
    })
  }
 const loadIngredientshandler = ingredients =>{
  setUserIngredients(ingredients);
 }
  const removeHandler = ( id ) =>{
    let idx = userIngredients.findIndex( el => el.id === id);
    userIngredients.splice(idx, 1);
    setUserIngredients([...userIngredients]);
}
  return (
    <div className="App">
      <IngredientForm ingredientHandler = {ingredientSubmitHandler}/>

      <section>
        <Search onLoadIngredients = {loadIngredientshandler}/>
        <IngredientsList ingredients = {userIngredients} onRemoveItem ={removeHandler}/>
      </section>
    </div>
  );
}

export default Ingredients;
